    def TambahMahasiswa(self):
        nim_baru=self.lineEdit_nim.text()
        nama_baru=self.lineEdit_nama.text()
        kelas_baru=self.lineEdit_kelas.text()
        alamat_baru=self.lineEdit_alamat.text()
        nohp_baru=self.lineEdit_no_hp.text()
        dosen_baru=self.comboBox.currentText()
        instansi_baru=self.comboBox_2.currentText()
        Mahasiswa.insert_mhs(nim_baru,nama_baru,kelas_baru,alamat_baru,nohp_baru,dosen_baru,instansi_baru)
        
        self.lineEdit_nim.clear()
