    def CariData(self):
        id=self.lineEdit_id.text()
        data=Instansi.select_data_by_id([id])
        nama=data[1]
        alamat=data[2]
        self.lineEdit_nama.setText(str(nama))
        self.lineEdit_alamat.setText(str(alamat))
        

    def UpdateData(self):
        id_baru=self.lineEdit_id.text()
        nama_baru=self.lineEdit_nama.text()
        alamat_baru=self.lineEdit_alamat.text()
        Instansi.update_data_instansi(nama_baru,alamat_baru,id_baru)
        self.lineEdit_id.clear()
        self.lineEdit_nama.clear()
        self.lineEdit_alamat.clear()
        

