    def CariData(self):
        id=self.lineEdit_id.text()
        data=Kelompok.select_data_by_id([id])
        nama=data[1]
        ketua=data[2]
        anggota_1=data[3]
        anggota_2=data[4]
        instansi=data[5]
        dosen=data[6]
        self.lineEdit_nama.setText(str(nama))
        self.lineEdit_ketua.setText(str(ketua))
        self.lineEdit_anggota1.setText(str(anggota_1))
        self.lineEdit_anggota2.setText(str(anggota_2))
        self.lineEdit_instansi.setText(str(instansi))
        self.lineEdit_nama_6.setText(str(dosen))

    def DeleteData(self):
        id = self.lineEdit_id.text()
        Kelompok.delete_kelompok(id)
        self.lineEdit_id.clear()
        self.lineEdit_nama.clear()
        self.lineEdit_ketua.clear()
        self.lineEdit_anggota1.clear()
        self.lineEdit_anggota2.clear()
        self.lineEdit_instansi.clear()
        self.lineEdit_nama_6.clear()

